def getStdDev(Arr):

    # CODE DEVELOPED/IMPLEMENTED BY
    #          Silvia Mazzoni, 2020
    #           smazzoni@ucla.edu

    import NGAsubGMM
    import math
    k1 = 1
    k2 = len(Arr)

    n = 0
    avg = NGAsubGMM.getMeanOfArray(Arr)
    SumSq = 0
    for i in range(k2):
        n = n + 1
        SumSq = SumSq + (Arr[i] - avg) ** 2
    # end

    getStdDevValue = math.sqrt(SumSq / (n - 1))
    return getStdDevValue


