# Cell
import matplotlib.pyplot as plt

# Cell
def set_plt_defaults(figsize=(10, 10), spines_off=['top','right']):
    plt.rcParams['figure.figsize'] = figsize
    for s in spines_off:
        plt.rcParams[f'axes.spines.{s}'] = False
